mapper.select:



		A.fecha_tipo_cambio = #{objetoMantenido.fechaTipoCambio}


		AND A.id_membresia = #{objetoMantenido.idMembresia}


ORDER BY A.fecha_tipo_cambio DESC, A.id_membresia ASC

{
    CALL PKG_CONSULTAS.PRC_CON_TIPO_CAMBIO_MEMBRESIA(
        #{modo,                                 jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.idMembresia,          jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.tipoCambio, 			jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.fechaTipoCambio, 		jdbcType=DATE, 	  mode=IN},
        #{usuario,                              jdbcType=VARCHAR, mode=IN}
    )
}
