mapper.select:



	  AND ID_REPORTE =#{idReporte}


	  AND FUN_REPORTE_AUTORIZADO(ID_REPORTE,#{usuario}) > 0


ORDER BY  ID_REPORTE ASC

{
    CALL PKG_MAN_REP.prc_man_rep_query(
        #{modo, 						  jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.idReporte,      jdbcType=INTEGER, mode=INOUT},
        #{objetoMantenido.nombre,  		  jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.descripcion, 	  jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.descCorta, 	  jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.frecuencia, 	  jdbcType=INTEGER, mode=IN},
        #{objetoMantenido.publico, 	  	  jdbcType=NUMERIC, mode=IN},
        #{usuario, 						  jdbcType=VARCHAR, mode=IN}
    )
}
