mapper.select:

 WHERE
			ID_RELACION_TABLA_FK > 0

			AND ID_TABLA =#{idTabla}


			AND fun_campo_permitido(ID_CAMPO_FORANEO,#{usuario})  > 0


			AND ID_RELACION_TABLA_FK=#{idRelacionTablaFk}

{
    CALL PKG_MAN_REP.prc_man_rep_rel_tabla_foranea(
        #{modo, 						  jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.idRelacionTablaFk,jdbcType=INTEGER, mode=INOUT},           
        #{objetoMantenido.idTabla,  	  jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.idCampo,        jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.idTablaForanea, jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.idCampoForaneo, jdbcType=NUMERIC, mode=IN},
        #{usuario, 						  jdbcType=VARCHAR, mode=IN}
    )
}
