mapper.select:

WHERE id_institucion = #{idInstitucion}
	AND id_clase_transaccion = #{idClaseTransaccion}
	AND id_codigo_transaccion = #{idCodigoTransaccion}

{
    CALL PKG_MAN_GENERALES.PRC_MAN_INST_X_TXN(
        #{modo,                                         jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.idInstitucion,                jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.idClaseTransaccion,			jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.idCodigoTransaccion,			jdbcType=NUMERIC, mode=IN},
        #{usuario,                              		jdbcType=VARCHAR, mode=IN}
    )
}
