mapper.select:

 WHERE
			ID_TABLA_ON_JOIN > 0

			AND ID_ON_JOIN =#{idTablaOnJoin}


			AND ID_REPORTE =#{idReporte}


			AND ID_TABLA_BASE =#{idTablaBase}


			AND ID_TABLA_JOIN =#{idTablaJoin}

{
    CALL PKG_MAN_REP.prc_man_rep_tabla_on_join(
        #{modo, 						    jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.idTablaOnJoin,    jdbcType=INTEGER, mode=INOUT},
        #{objetoMantenido.idCampoQueryBase, jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.idCampoQueryJoin, jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.idOperador ,      jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.operadorLogico ,  jdbcType=VARCHAR, mode=IN},
        #{usuario, 						  jdbcType=VARCHAR, mode=IN}
    )
}
