mapper.select:



			AND ID_TABLA_QUERY =#{idTablaQuery}


			AND ID_REPORTE =#{idReporte}


			AND ID_TABLA =#{idTabla}


			AND fun_tabla_permitida(ID_TABLA,#{usuario})  > 0


 ORDER BY ID_TABLA_QUERY

{
    CALL PKG_MAN_REP.prc_man_rep_tabla_query(
        #{modo, 						  jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.idTablaQuery,   jdbcType=INTEGER, mode=INOUT},
        #{objetoMantenido.idReporte,      jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.idTabla,  	  jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.idInstancia,    jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.orden , 	      jdbcType=NUMERIC, mode=IN},
        #{objetoMantenido.esBase , 	      jdbcType=NUMERIC, mode=IN},
         #{objetoMantenido.idTipoJoin,    jdbcType=NUMERIC, mode=IN},
        #{usuario, 						  jdbcType=VARCHAR, mode=IN}
    )
}
