mapper.select:

WHERE
        id_membresia  = #{idMembresia}
    AND id_producto = #{idProducto}
    AND id_proceso = #{idProceso}

{
    CALL PKG_MAN_GENERALES.PRC_MAN_LIBERACION_MARCA(
        #{modo, 							jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.idMembresia, 	   	jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.idProducto, 	   	jdbcType=INTEGER, mode=IN},
        #{objetoMantenido.idProceso, 	  	jdbcType=VARCHAR, mode=IN},
        #{objetoMantenido.diasLiberacion,	jdbcType=INTEGER, mode=IN},
        #{usuario, 							jdbcType=VARCHAR, mode=IN}
    )
}
